//INPUT PROPS
//webServerBase - Base URL of web server
var App = React.createClass({displayName: "App",
  render: function() {
    var windowHeight  = $(window).innerHeight();
    var windowWidth   = $(window).innerWidth();
    var styles = {backgroundColor: "green",
                  width:            windowWidth,
                  height:           windowHeight};
    //return <div style={styles}>This was generated by React</div>;
    return React.createElement("div", {style: styles}, 
            React.createElement("p", null, "Window Height: ", windowHeight), 
            React.createElement("p", null, "Window Width: ", windowWidth), 
            React.createElement(WelcomePage, {webServerBase: this.props.webServerBase})
           );
  }
});
